        iconContainer.classList.toggle('hidden');
    })
    function Image(image) {
        const icons = document.querySelectorAll('#iconContainer img');
        icons.forEach(icon => {
            if (icon.id === selectedId) {
                // Hacer el ícono seleccionado más oscuro
